                attachEvt("result", "allRecordsReceived", function () {
                    if(!resultFacetKey) return;
                    if (fblob.facetCacheKey) return; // already rendered it

                    dojo.removeClass('facetSidebarContainer','hide_me');

                    fblob.facetCacheKey = resultFacetKey;
                    fblob.render();
                });










